import { useSelector } from "react-redux";

import cl from "./Article.module.scss";

type RootState = {
  articlesReducer: {
    data: {
      articles: {
        author: {
          username: string;
          image: string;
          following: false;
        };

        body: string;
        createdAt: string;
        favorited: boolean;
        favoritesCount: number;
        slug: string;
        tagList: string[];
        title: string;
        updatedAt: string;
      }[];
    };
  };
};

export const Article: React.FC = () => {
  const articles = useSelector(
    (state: RootState) => state.articlesReducer.data.articles
  );

  const getArticle =
    articles &&
    articles.map((item) => {
      return (
        <li className={cl["articles-list__item"]}>
          <div className="articles-list__item__header">
            <div className="articles-list__item__header__left-content">
              <h1 className="articles-list__item__header__left-content__title"></h1>
              <div className="articles-list__item__header__left-content__likes"></div>
              {item.tagList.map()}
            </div>
            <div className="articles-list__item__header__right-content"></div>
          </div>
        </li>
      );
    });

  return <li></li>;
};
