import { useSelector } from "react-redux";

import cl from "./Article.module.scss";

import { isDate } from "date-fns";

type RootState = {
  articlesReducer: {
    data: {
      articles: {
        author: {
          username: string;
          image: string;
          following: boolean;
        };

        body: string;
        createdAt: string;
        favorited: boolean;
        favoritesCount: number;
        slug: string;
        tagList: string[];
        title: string;
        updatedAt: string;
      }[];
    };
  };
};

export const Article: React.FC = () => {
  const articles = useSelector(
    (state: RootState) => state.articlesReducer.data.articles
  );

  const getArticle =
    articles &&
    articles.map((item) => {
      return (
        <li className={cl["articles-list__item"]}>
          <div className="articles-list__item__header">
            <div className="articles-list__item__header__left-content">
              <h1 className="articles-list__item__header__left-content__title"></h1>
              <div className="articles-list__item__header__left-content__likes"></div>
              {item.tagList.map((tag) => (
                <span key={tag}>{tag}</span>
              ))}
            </div>
            <div className="articles-list__item__header__right-content">
              <p className="articles-list__item__header__right-content__username-author">
                {item.author.username}
              </p>
              <p className="articles-list__item__header__right-content__createdAt">
                {item.createdAt}
              </p>
              <img src={item.author.image}></img>
            </div>
          </div>
        </li>
      );
    });

  return <>{getArticle}</>;
};
